#include "SoPhuc.h"SoPhuc::SoPhuc(){	this->phanThuc = phanThuc;	this->phanAo = phanAo;}SoPhuc::SoPhuc(double phanThuc, double phanAo){	this->phanThuc = phanThuc;	this->phanAo = phanAo;}double SoPhuc::LayPhanThuc(){	return phanThuc;}double SoPhuc::LayPhanAo(){	return phanAo;}void SoPhuc::DatPhanThuc(double phanThuc){	this->phanThuc = phanThuc;}void SoPhuc::DatPhanAo(double phanAo){	this->phanAo = phanAo;}void SoPhuc::NhapSoPhuc(){	cout << "Nhap phan thuc: ";	cin >> phanThuc;	cout << "Nhap phan ao: ";	cin >> phanAo;}void SoPhuc::XuatSoPhuc(){	if (phanAo < 0)	{		cout << phanThuc << " - " << -phanAo << "i";	}	else if (phanAo == 0)	{		cout << phanThuc;	}	else if (phanAo > 0)	{		cout << phanThuc << " + " << phanAo << "i";	}}SoPhuc SoPhuc::TinhTong(SoPhuc& a){	SoPhuc b;	b.phanThuc = a.phanThuc + this->phanThuc;	b.phanAo = a.phanAo + this->phanAo;	return b;}SoPhuc SoPhuc::TinhHieu(SoPhuc& a){	SoPhuc b;	b.phanThuc = a.phanThuc - this->phanThuc;	b.phanAo = a.phanAo - this->phanAo;	return b;}SoPhuc SoPhuc::TinhTich(SoPhuc& a){	SoPhuc b;	b.phanThuc = a.phanThuc * this->phanThuc - a.phanAo * this->phanAo;	b.phanAo = a.phanThuc * this->phanAo + a.phanAo * this->phanThuc;	return b;}SoPhuc SoPhuc::TinhThuong(SoPhuc& a){	SoPhuc b;	b.phanThuc = (a.phanThuc * this->phanThuc + a.phanAo * this->phanAo) / (pow(this->phanThuc, 2) + pow(this->phanAo, 2));	b.phanAo = (this->phanThuc * a.phanAo - a.phanThuc * this->phanAo) / (pow(this->phanThuc, 2) + pow(this->phanAo, 2));	return b;}